CREATE TABLE IF NOT EXISTS smoothie (id SERIAL PRIMARY KEY, name VARCHAR(255), jus VARCHAR(255), description VARCHAR(255));
CREATE TABLE IF NOT EXISTS fruit (id SERIAL PRIMARY KEY, name VARCHAR(255), type VARCHAR(255), preparation VARCHAR(255));
CREATE TABLE IF NOT EXISTS smoothie_fruit (id SERIAL PRIMARY KEY, id_smoothie INTEGER, id_fruit INTEGER, FOREIGN KEY(id_smoothie) REFERENCES smoothie(id), FOREIGN KEY(id_fruit) REFERENCES fruit(id));
CREATE TABLE IF NOT EXISTS fruit_gout (id SERIAL PRIMARY KEY, id_fruit INTEGER, gout VARCHAR(255), FOREIGN KEY(id_fruit) REFERENCES fruit(id));